import React from "react";
import { generateDescriptions } from "../openAi/apiGateway";
import { hideLoading, showLoading, Loading } from "./Loading";

export default class DescriptionSection extends React.Component {
    constructor(props) {
        super(props)
    }


    render() {
        return (
            <div className="card mt-2">
                <Loading />
                <div className='card-body'>
                    <h5 className="card-title">Meta Description</h5>
                    <h6 className="card-subtitle mb-2 text-muted">Which meta description best describes your content?</h6>
                    <p className="card-text">Target keywords: <b className='text-info'>{this.props.params.keywords}</b>. Your meta description should explain to search engines and visitors what your page is about.</p>
                    <div className='col-md-6 mb-2'>
                        <button onClick={async () => {
                            showLoading()
                            const descs = await generateDescriptions(this.props.params.apiKey, this.props.params.title)
                            this.props.params.getGeneratedDescriptions(descs);
                            console.log(descs)
                            hideLoading()
                        }} type="button" className="btn btn-sm btn-warning">NEXT: META DESCRIPTION</button>
                    </div>
                    <div className='row row-cols-1 row-cols-md-3 g-4 mb-2' id='titleSection'>{this.props.params.suggestedDescriptions}</div>

                </div>
                <div className='card-footer'>
                    <p><b>Meta Description</b>: {this.props.params.metaDescription}</p>
                </div>
            </div >
        )
    }
}
