import React from "react";
import { v4 as uuidv4 } from 'uuid';
import ButtonCopy from "./ButtonCopy";

export default class RadioButton extends React.Component {

    render() {
        let id = uuidv4();
        valueTXT = this.props.value
        return (

            <div>
                <div className="col-md-6 col-lg-12 col-sm-12">
                    <div className="input-group input-group-sm mb-3">
                        <div class="input-group-text">
                            <input className="form-check-input" onChange={(e) => { this.props.getSelectdTtile(e.target.value) }} value={this.props.value} type="radio" name={this.props.name} id={id} />
                        </div>
                        <textarea className="form-control" aria-label="With textarea" defaultValue={valueTXT}></textarea>
                        <ButtonCopy value={this.props.value} />
                    </div>
                </div>
            </div>


        )
    }

}
