import React from "react";
import { v4 as uuidv4 } from 'uuid';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

export default class RadioButton extends React.Component {

    render() {
        let id = uuidv4();
        return (
            <div className="col">
                <ToastContainer />

                <div className="card p-2 m-2 h-100" >
                    <div className="text-end">
                        <button type="button" className="btn btn-light" onClick={(e) => {
                            navigator.clipboard.writeText(this.props.value);
                            toast.success('Text Copied', {
                                position: "bottom-right",
                                autoClose: 5000,
                                hideProgressBar: false,
                                closeOnClick: true,
                                pauseOnHover: true,
                                draggable: true,
                                progress: undefined,
                            });
                        }}>
                            <span className="material-symbols-outlined">content_copy</span>
                        </button>
                    </div>
                    <div className="form-check">
                        <input className="form-check-input" onChange={(e) => { this.props.getSelectdTtile(e.target.value) }} value={this.props.value} type="radio" name={this.props.name} id={id} />
                        <label className="form-check-label" forhtml={id}>
                            {this.props.value}
                        </label>
                    </div>
                </div>
            </div>
        )
    }

}
