import { Configuration, OpenAIApi } from "openai"


async function validateApiKey(apiKey) {

    const configuration = new Configuration({
        apiKey: apiKey,
    });
    const openai = new OpenAIApi(configuration);

    try {
        const completion = await openai.createCompletion({
            model: "text-davinci-002",
        });
        console.log("result" + completion.data.choices[0].text)
    } catch (error) {
        console.log('False')
        return false;
    }
    return true
}

async function generateTitles(apiKey, keywords) {
    return "-Car vs. Motorcycle: The Pros and Cons  -The debate of Car vs. Motorcycle: Which is better? -Advantages and disadvantages of Cars vs Motorcycles -Cars vs Motorcycles: A Comprehensive Comparison -The Pros and Cons of Cars and Motorcycles"
    const configuration = new Configuration({
        apiKey: apiKey,
    });
    const openai = new OpenAIApi(configuration);

    try {
        const completion = await openai.createCompletion({
            model: "text-davinci-002",
            prompt: "Generate blog topics on: " + keywords + "and sort them by hyphen",
            temperature: 0.7,
            max_tokens: 100,
            top_p: 1,
            frequency_penalty: 0,
            presence_penalty: 0
        });
        return completion.data.choices[0].text
    } catch (error) {
        if (error.response) {
            console.error(error.response.status);
            console.error(error.response.data);
        } else {
            console.error(error)
        }
        return false
    }
}

async function generateDescriptions(apiKey, title) {
    const configuration = new Configuration({
        apiKey: apiKey,
    });
    const openai = new OpenAIApi(configuration);

    try {
        const completion = await openai.createCompletion({
            model: "text-davinci-002",
            prompt: "Generate multiple blog short descriptions on :" + title + " and sort them by hyphen",
            temperature: 0.7,
            max_tokens: 400,
            top_p: 1,
            frequency_penalty: 0,
            presence_penalty: 0
        });
        console.log(completion.data.choices[0].text)
        return completion.data.choices[0].text

    } catch (error) {
        if (error.response) {
            console.error(error.response.status);
            console.error(error.response.data);
        } else {
            console.error(error)
        }
        return false
    }
}

export { validateApiKey, generateTitles, generateDescriptions }
