    render() {
        let id = uuidv4();
        return (

            <div>
                <div className="col-md-6 col-lg-12 col-sm-12">
                    <div className="input-group input-group-sm mb-3">
                        <div class="input-group-text">
                            <input type="radio" aria-label="Radio button for following text input" />
                        </div>
                        {/* <input type="text" className="form-control" value={this.props.value} /> */}
                        <textarea class="form-control" aria-label="With textarea">value={this.props.value}</textarea>
                        <ButtonCopy value={this.props.value} />
                    </div>
                </div>
                <div class="input-group">
                    {/* <div className="card h-100 text-12" >
