import React from "react";

import { toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

export default class ApiValidation extends React.Component {

    constructor(props) {
        super(props)
    }

    render() {
        let notifyError = () => toast.error('Api Error! Check that the API Key is valid and not reached the limit', {
            position: "bottom-right",
            autoClose: 5000,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,
        });
        if (this.props.apiKey == null){
            console.log("YES it's Invalid")
            return { notifyError }
        }
    }

}
