import React from 'react';

import './App.css';
import KeywordSection from './components/KeywordsSection';
import ApiKeyValidationSection from './components/ApiKeyValidationSection';
import RadioButton from './components/RadioButton';
import TitlesSection from './components/TitlesSection';
import { v4 as uuidv4 } from 'uuid';

export default class App extends React.Component {

  constructor(props) {
    super(props)
    this.state = {
      apiKey: null,
      keywords: null,
      title: null,
      suggestedTitles: [],
      metaDescription: null
    }

    this.getApiKey = this.getApiKey.bind(this)
    this.getKeywords = this.getKeywords.bind(this)
    this.getGeneratedTitles = this.getGeneratedTitles.bind(this)
    this.showTitlesSection = this.showTitlesSection.bind(this)
  }


  getApiKey = async (data) => {
    this.setState({ apiKey: data })
  }
  getKeywords = async (data) => {
    this.setState({ keywords: data })
  }

  getGeneratedTitles = async (data) => {
    this.setState({ title: data }, () => {
      this.showTitlesSection(data);
      this.setState({ suggestedTitles: this.showTitlesSection(data) })
    })
  }

  showTitlesSection(data) {
    const titles = data.split('-');
    let endata = []
    titles.forEach((e,key) => {
      endata.push(
        <RadioButton key={key} name="titles" value={e} getSelectdTtile={this.getSelectdTtile} />
      )
    });
    this.setState({ title: null })
    return endata
  }

  getSelectdTtile = (data) => {
    this.setState({ title: data })
  }

  render() {
    return (
      <div className="App container">
        <ApiKeyValidationSection getApiKey={this.getApiKey} />
        <KeywordSection apiKey={this.state.apiKey} getGeneratedTitles={this.getGeneratedTitles} getKeywords={this.getKeywords} />
        <TitlesSection keywords={this.state.keywords} suggestedTitles={this.state.suggestedTitles} title={this.state.title} />
        <div className="card mt-2">
          <div className='card-body'>
            <h5 className="card-title">Meta Description</h5>
            <h6 className="card-subtitle mb-2 text-muted">Which meta description best describes your content?</h6>
            <p className="card-text">Target keywords: <b className='text-info'>{this.state.keywords}</b>. Your meta description should explain to search engines and visitors what your page is about.</p>
          </div>
          <div className=' card-footer  text-end'>
            <div className='row'>
              <div className='col-md-6 h-md-100 text-start'>
                <p><b>Meta Description</b>: {this.props.title}</p>
              </div>
              <div className='col-md-6 text-end'>
                <button type="button" className="btn btn-warning">NEXT: CHOOSE HEADINGS</button>
              </div>
            </div>
          </div>
        </div>

        <section>
          <h1>Which headings you would like to start out with?</h1>
          <h3>Your headings are like chapters of your content, and indicate what visitors will read at each section.</h3>
          <input placeholder='Enter a keyword [i.e. "meal delivery"]' type="checkbox"></input>
          <button>WRITE FOR ME</button>
        </section>
      </div>
    )
  }
}
