import React from "react";
import { v4 as uuidv4 } from 'uuid';
export default class RadioButton extends React.Component {
    render() {
        let id = uuidv4();
        return (
            <div className="col">
                <div className="card p-2 m-2 h-100" >
                    <div>
                        <button type="button" className="btn btn-light" onClick={(e) => { navigator.clipboard.writeText(this.props.value); e.target.innerHTML = "Copied" }}><i class="fa fa-clone" /></button>
                    </div>
                    <div className="form-check">
                        <input className="form-check-input" onChange={(e) => { this.props.getSelectdTtile(e.target.value) }} value={this.props.value} type="radio" name={this.props.name} id={id} />
                        <label className="form-check-label" forhtml={id}>
                            {this.props.value}
                        </label>
                    </div>
                </div>
            </div>
        )
    }

}
