import { Configuration, OpenAIApi } from "openai"


async function validateApiKey(apiKey) {

    const configuration = new Configuration({
        apiKey: apiKey,
    });
    const openai = new OpenAIApi(configuration);

    try {
        const completion = await openai.createCompletion({
            model: "text-davinci-002",
        });
        console.log("result" + completion.data.choices[0].text)
    } catch (error) {
        console.log('False')
        return false;
    }
    return true
}

async function generateTitles(apiKey, keywords) {
    // return "uniforms - Are school uniforms a good or bad thing? - The pros and cons of school uniforms. - How do school uniforms affect students? - Do school uniforms help or hinder learning? - The benefits of school uniforms. - The drawbacks of school uniforms. - Are school uniforms necessary? - Do school uniforms promote equality? - Do school uniforms stifle individuality? - Why do some schools require uniforms"

    const configuration = new Configuration({
        apiKey: apiKey,
    });
    const openai = new OpenAIApi(configuration);

    try {
        const completion = await openai.createCompletion({
            model: "text-davinci-002",
            prompt: "Generate blog topics on: {}" + keywords + "and sort them hyphen",
            temperature: 0.7,
            max_tokens: 100,
            top_p: 1,
            frequency_penalty: 0,
            presence_penalty: 0
        });
        return completion.data.choices[0].text
    } catch (error) {
        if (error.response) {
            console.error(error.response.status);
            console.error(error.response.data);
        } else {
            console.error(error)
        }
        return false
    }
}

export { validateApiKey, generateTitles }
