import React from 'react';
import ReactDOM from 'react-dom/client';

import './App.css';
import KeywordSection from './components/KeywordsSection';
import ApiKeyValidationSection from './components/ApiKeyValidationSection';
import RadioButton from './components/RadioButton';

export default class App extends React.Component {

  constructor(props) {
    super(props)
    this.state = {
      apiKey: null,
      keywords: null,
      title: null,
      entries: []
    }

    this.getApiKey = this.getApiKey.bind(this)
    this.getKeywords = this.getKeywords.bind(this)
    this.getGeneratedTitles = this.getGeneratedTitles.bind(this)
    this.showTitlesSection = this.showTitlesSection.bind(this)
  }


  getApiKey = async (data) => {
    this.setState({ apiKey: data })
  }
  getKeywords = async (data) => {
    this.setState({ keywords: data })
  }

  getGeneratedTitles = async (data) => {
    this.setState({ title: data }, () => {
      this.showTitlesSection(data);
      this.setState({ entries: this.showTitlesSection(data) })
    })
  }

  showTitlesSection(data) {
    const titles = data.split('-');
    let endata = []
    titles.forEach(e => {
      endata.push(
        <RadioButton name="titles" value={e} />
      )
    });
    return endata
  }

  render() {
    return (
      <div className="App container">
        <div className="card mt-2">
          <div className='card-body'>
            <h5 className="card-title">OpenAi API Key </h5>
            <ApiKeyValidationSection getApiKey={this.getApiKey} />
          </div>
        </div>
        <div className="card mt-2">
          <div className='card-body'>
            <KeywordSection apiKey={this.state.apiKey} getGeneratedTitles={this.getGeneratedTitles} getKeywords={this.getKeywords} />
            <div id='titleSection'>{this.state.entries}</div>
          </div>
        </div>
        <div className="card mt-2">
          <div className='card-body'>
            <h5 className="card-title">Titles</h5>
            <div className="crad-text">Which title best represents what your page is about?</div>
            <h3>Target keyword: <b>{this.state.keywords}</b>. Your Title should read naturally and grab the reader's attention.</h3>
            <h2>Generated Page Title Ideas</h2>
            <input placeholder='Enter a keyword [i.e. "meal delivery"]' type="radio"></input>
            <button>NEXT: META DESCRIPTION</button>
          </div>
        </div>
        <section>
          <h1>Which meta description best describes your content?</h1>
          <h3>Target keyword: <b>SMS ONLINE</b>. Your meta description should explain to search engines and visitors what your page is about. </h3>
          <h2>Generated Meta Description Ideas</h2>
          <input placeholder='Enter a keyword [i.e. "meal delivery"]' type="radio"></input>
          <button>NEXT: CHOOSE HEADINGS</button>
        </section>
        <section>
          <h1>Which headings you would like to start out with?</h1>
          <h3>Your headings are like chapters of your content, and indicate what visitors will read at each section.</h3>
          <input placeholder='Enter a keyword [i.e. "meal delivery"]' type="checkbox"></input>
          <button>WRITE FOR ME</button>
        </section>
      </div>
    )
  }
}
