import React from "react";

import { toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

export default class ApiValidation extends React.Component {


     notifyError = () => toast.error('Api Error! Check that the API Key is valid and not reached the limit', {
        position: "bottom-right",
        autoClose: 5000,
        hideProgressBar: false,
        closeOnClick: true,
        pauseOnHover: true,
        draggable: true,
        progress: undefined,
    });
    render() {

        console.log("YES it's Invalid"+ this.props.apiKey)

        if (this.props.apiKey === null){
            console.log("YES it's Invalid")
            return (this.notifyError())
        }
    }

}
