import React from "react";
import { showLoading } from "./Loading";

export default class Blog extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            headings: [],
            sections: [],
            fullBlog: "",
            blogJSX: [],
        }
    }

    pushToHeadings(data) {
        this.setState(prevState => ({
            headings: [...prevState.headings, data]
        }))
    }

    pushToSections(data) {
        this.setState(prevState => ({
            headings: [...prevState.headings, data]
        }))
    }

    pushToBlogJSX(heading, section) {
        let data = (
            <div>
                <h1>{heading}</h1>
                <p>{section}</p>
            </div>
        )
        this.setState(prevState => ({
            blogJSX: [...prevState.blogJSX, data]
        }))
    }

    render() {
        return (
            <div className="card mt-2">
                <div className='card-body'>
                    <div>
                        <Loading />
                        <button type="button" className="btn btn-warning"
                            onClick={() => {
                                showLoading()
                                let arr = this.props.headingsheadings
                                for (i = 0; i < arr.length; i++) {
                                    this.pushToHeadings(arr[i])
                                    let section = async () => generateBlog(this.props.apiKey, arr[i])
                                    this.pushToSections(section)
                                    this.pushToBlogJSX(arr[i], section)
                                }
                            }}>
                            WRITE FOR ME
                        </button>
                        <div className="card">
                            {this.state.blogJSX}
                        </div>
                    </div>
                </div>
            </div>
        )
    }
}
