import React from "react";
import { generateHeadings } from "../../openAi/apiGateway";
import { showLoading, hideLoading, Loading } from "../elements/Loading";

export default class HeadingsSection extends React.Component {

    constructor(props) {
        super(props)
        this.state = {

        }
    }

    render() {
        return (
            <div className="card mt-2">
                <Loading/>
                <div className='card-body'>
                    <div>
                        <span className=" h6 card-title align-middle mb-2">Headings</span>
                        <p className="card-subtitle mb-2 text-muted">Which headings you would like to start out with?</p>
                        <p className="card-text">Your headings are like chapters of your content, and indicate what visitors will read at each section.</p>
                        <button type="button" className="btn btn-warning" onClick={async () => {
                            showLoading()
                            const headings = await generateHeadings(this.props.params.apiKey, this.props.params.title)
                            this.props.params.getGeneratedHeadings(headings)
                            console.log(headings)
                            hideLoading()
                        }}>NEXT: CHOOSE HEADINGS</button>
                        <div className='row row-cols-1 row-cols-md-3 g-4 mb-2' id='titleSection'>{this.props.params.suggestedHeadings}</div>
                    </div>
                </div>
                <div className=' card-footer  text-end'>
                    <div className='row'>
                        <div className='col-md-6 h-md-100 text-start'>
                            <p><b>You selected</b>: <span className="text-info">{this.props.params.headings.length}</span> Headings </p>
                        </div>
                    </div>
                </div>
            </div>
        )
    }
}
