import React from 'react';
import './App.css';
import KeywordSection from './components/KeywordSection';
import { validateApiKey } from './openAi/apiGateway';

export default class App extends React.Component {

  constructor(props) {
    super(props)
    this.alert = this.alert.bind(this);
    this.showLoading = this.alert.bind(this);
  }


  apiKey = 'sk-b2EFymJXfFoU7ZZeXA07T3BlbkFJmrfsLLkPXg7y0zNNjuY'

  alert(message, type) {
    const wrapper = document.createElement('div')
    wrapper.innerHTML = [
      `<div class="alert alert-${type} alert-dismissible" role="alert">`,
      `   <div>${message}</div>`,
      '   <button type="button" class="btn-close" data-bs-dismiss="alert" aria-label="Close"></button>',
      '</div>'
    ].join('')

    const alertPlaceholder = document.getElementById('liveAlertPlaceholder')
    alertPlaceholder.append(wrapper)
  }

  showLoading() {
    document.getElementById('loader').style.visibility = "visible"
  }
  hideLoading() {
    document.getElementById('loader').style.visibility = "hidden"
  }

  render() {
    return (

      <div id="loader" className="App container m-3">
        <div class="loader-div">
          <span class="loader">
            <span></span>
            <span></span>
          </span>
        </div>

        <div id="liveAlertPlaceholder"></div>
        <div className="input-group mb-3">
          <input onChange={e => { this.apiKey = e.target.value }} placeholder={this.apiKey} type="text" className="form-control" aria-label="Recipient's username" aria-describedby="button-addon2" />
          <button
            onClick={  async () => { 
              this.showLoading()
              await validateApiKey(this.apiKey) === true
                ? this.alert('Working like a charm :) happy hacking', 'success')
                : this.alert('Error occured! Check that the API Key is valid and not reached the limit', 'danger')
            }}
            className="btn btn-outline-secondary"
            type="submit"
            id="button-addon2">Validate API Key</button>
        </div>

        <span>Writing new content automagically!</span>
        <KeywordSection s />
        <section>
          <h1>Which title best represents what your page is about?</h1>
          <h3>Target keyword: <b>SMS ONLINE</b>. Your Title should read naturally and grab the reader's attention.</h3>
          <h2>Generated Page Title Ideas</h2>
          <input placeholder='Enter a keyword [i.e. "meal delivery"]' type="radio"></input>
          <button>NEXT: META DESCRIPTION</button>
        </section>
        <section>
          <h1>Which meta description best describes your content?</h1>
          <h3>Target keyword: <b>SMS ONLINE</b>. Your meta description should explain to search engines and visitors what your page is about. </h3>
          <h2>Generated Meta Description Ideas</h2>
          <input placeholder='Enter a keyword [i.e. "meal delivery"]' type="radio"></input>
          <button>NEXT: CHOOSE HEADINGS</button>
        </section>
        <section>
          <h1>Which headings you would like to start out with?</h1>
          <h3>Your headings are like chapters of your content, and indicate what visitors will read at each section.</h3>
          <input placeholder='Enter a keyword [i.e. "meal delivery"]' type="checkbox"></input>
          <button>WRITE FOR ME</button>
        </section>
      </div>
    )
  }
}
