import React from "react";
import { generateDescriptions } from "../openAi/apiGateway";
import { hideLoading, showLoading, Loading } from "./Loading";
export default class TitlesSection extends React.Component {

    customTitle = ""
    render() {
        return (
            <div className="card mt-2">
                <Loading />
                <div className='card-body'>
                    <div className="row mb-2">
                        <div className="col-8">
                            <h6 className="card-title">Titles</h6>
                        </div>
                        <div className="col-4 text-end">
                            <button type="button" className="btn btn-sm btn-warning">GENERATE TITLES</button>
                        </div>
                    </div>
                    <p className="card-subtitle mb-2 text-muted"><small>Which title best represents what your page is about?</small></p>

                    <span className="font-weight-light font-italic">Target keywords: <b className='text-info'>{this.props.keywords}</b>. Your Title should read naturally and grab the reader's attention.</span>
                    <div className="row">
                        <div className="col-md-6 col-sm-12">
                            <div className="input-group input-group-sm mb-2">
                                <input onChange={e => { this.customTitle = e.target.value }} placeholder="Set your custom title here" type="text" className="form-control" aria-label="Custom Title" aria-describedby="button-addon2" />
                                <button
                                    onClick={async () => {
                                        this.props.getCustomTitle(this.customTitle);
                                    }}
                                    className="btn btn-warning"
                                    type="submit"
                                    id="button-addon2">Set Title</button>
                            </div>
                        </div>
                    </div>
                    {/* <p className="text-muted mt-2 mb-1">Generated Page Title Ideas</p> */}
                    <div className='row row-cols-1 row-cols-md-3' id='titleSection'>{this.props.suggestedTitles}</div>


                </div>
                <div className=' card-footer text-muted  text-end'>
                    <div className='row'>
                        <div className='col-md-6 h-md-100 text-start'>
                            <p><small><b>Title</b>: {this.props.title}</small></p>
                        </div>
                        <div className='col-md-6 text-end'>
                            <button onClick={async () => {
                                showLoading()
                                const descs = await generateDescriptions(this.props.apiKey, this.props.title)
                                this.props.getGeneratedDescriptions(descs);
                                console.log(descs)
                                hideLoading()
                            }} type="button" className="btn btn-sm btn-warning">NEXT: META DESCRIPTION</button>
                        </div>
                    </div>
                </div>
            </div>
        )
    }
}
