import React from "react";
import { Configuration, OpenAIApi } from "openai"
import { Loading, showLoading, hideLoading } from './Loading';
import { generateTopics } from "../openAi/apiGateway";
export default class KeywordSection extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            error: null,
            isLoaded: false,
            result: null,
        }
        this.generateKeyword = this.generateKeyword.bind(this);
    }

    async generateKeyword() {
        const configuration = new Configuration({
            apiKey: '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',
        });
        const openai = new OpenAIApi(configuration);

        try {
            const completion = await openai.createCompletion({
                model: "text-davinci-002",
                prompt: "Hello world",
            });
            this.setState({ isLoaded: true, result: completion.data.choices[0].text })
            console.log(completion.data.choices[0].text)
        } catch (error) {
            if (error.response) {
                console.error(error.response.status);
                console.error(error.response.data);
                this.setState({ isLoaded: true, error: error })
            } else {
                console.error(error)
                this.setState({ isLoaded: true, error: error.message })
            }
        }
    }


    render() {
        return (
            <div>
                <Loading />
                <h5 class="card-title">Writing new content automagically!</h5>
                <div className="crad-text">Tell me what you want to rank for. I'll help create your content.</div>
                <input placeholder='Enter a keyword [i.e. "meal delivery"]'></input>
                <div className="input-group mb-3">
                    <input onChange={e => { this.apiKey = e.target.value }} placeholder={this.apiKey} type="text" className="form-control" aria-label="Recipient's username" aria-describedby="button-addon2" />
                    <button
                        onClick={async () => {
                            showLoading()
                            await generateTopics (this.apiKey) === true
                                ? this.alert('Working like a charm :) happy hacking', 'success')
                                : this.alert('Error occured! Check that the API Key is valid and not reached the limit', 'danger')
                            hideLoading()
                        }}
                        className="btn btn-outline-secondary"
                        type="submit"
                        id="button-addon2">Start</button>
                </div>
            </div>
        )
    }

}
