import React from "react";
import { showLoading, hideLoading, Loading } from "../elements/Loading";
import { generateBlog } from "../../openAi/apiGateway";
import ButtonCopy from "../elements/ButtonCopy";
export default class Blog extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            headings: [],
            sections: [],
            fullBlog: "",
            blogJSX: [],
        }
    }

    pushToHeadings(data) {
        this.setState(prevState => ({
            headings: [...prevState.headings, data]
        }))
    }

    pushToSections(data) {
        this.setState(prevState => ({
            headings: [...prevState.headings, data]
        }))
    }

    pushToBlogJSX(heading, section) {
        let data = (
            <div>
                <h2 className="text-capitalize">{heading} <ButtonCopy value={heading} /></h2>
                <p>{section}  <ButtonCopy value={section} /></p>
            </div>
        )
        this.setState(prevState => ({
            blogJSX: [...prevState.blogJSX, data]
        }))
    }

    textAreaAdjust(el) {
        el.style.height = (el.scrollHeight > el.clientHeight) ? (el.scrollHeight)+"px" : "60px";
    }

    render() {
        return (
            <div className="card mt-2">
                <Loading />
                <div className='card-body'>
                    <div>
                        <button type="button" className="btn btn-sm btn-warning"
                            onClick={async () => {
                                if (this.props.params.apiKey===null) {
                                    return alert("API Not Valid") 
                                }
                                let arr = this.props.params.headings
                                let text = ""
                                for (let i = 0; i < arr.length; i++) {
                                    // showLoading()
                                    this.pushToHeadings(arr[i])
                                    const section = await generateBlog(this.props.params.apiKey, arr[i], this.props.params.title)
                                    console.log(section)
                                    this.pushToSections(section)
                                    this.pushToBlogJSX(arr[i], section)
                                    // hideLoading()

                                    text = text + arr[i] + '\n' + section + '\n'
                                }
                                // this.setState({ fullBlog: text })
                                this.setState({ fullBlog: "This is the text:" + text }, () => {
                                    console.log(this.state.fullBlog)
                                    // document.getElementById('fullBlogArea').focus()
                                    document.getElementById("fullBlogArea").focus();
                                })
                            }}>
                            WRITE FOR ME
                        </button>

                        <div className="card">
                            <b className="h6">{this.props.params.title} <ButtonCopy value={this.props.params.title} /></b>
                            <p>{this.props.params.metaDescription} <ButtonCopy value={this.props.params.metaDescription} /></p>
                            {this.state.blogJSX}
                            <textarea id="fullBlogArea" onFocus={(e)=>this.textAreaAdjust(e.target)} defaultValue={this.state.fullBlog}></textarea>

                        </div>
                    </div>
                </div>
            </div>
        )
    }
}
